mapper.update:
UPDATE lysl_demand_object
demand_id = #{demandId}, institution_id = #{institutionId}, donee_id = #{doneeId}, material_name = #{materialName}, material_id = #{materialId}, material_num = #{materialNum}, status = #{status}, description = #{description}, gmt_modified = now()
WHERE id = #{id}

mapper.select:
select

from lysl_demand_object
where status = 'UNCHECKED' AND institution_id = #{param1}

SELECT *
FROM lysl_demand_object

    AND demand_id = #{demandId}
    AND status = #{status}
    AND institution_id = #{institutionId}
